    $('#input-target-date').datepicker({
      format: 'yyyy-mm-dd',
      autoclose: true,
      startDate: new Date(),
      language: 'kr'
    }).on('changeDate', function(e) {
      var additional_days, additional_fee;
      additional_days = overdue_calc(new Date(), $('#input-target-date').datepicker('getDate'));
      additional_fee = additional_days * origin_fee * 0.2;
      return $('#input-price').val(parseInt(origin_fee) + parseInt(additional_fee));
    });
    overdue_calc = function(target_dt, return_dt) {
      var DAY_AS_SECONDS, dur;
      DAY_AS_SECONDS = 60 * 60 * 24;
      dur = return_dt.getTime() - (DAY_AS_SECONDS * 1000 * 2) - target_dt.getTime();
      if (dur < 0) {
        return 0;
      }
      return parseInt(dur / 1000 / DAY_AS_SECONDS);
    };
    $('#btn-order-cancel:not(.disabled)').click(function(e) {
      var $this;
      e.preventDefault();
      $this = $(this);
      $this.addClass('disabled');
      return $.ajax("" + ($this.attr('href')) + ".json", {
        type: 'DELETE',
        dataType: 'json',
        success: function(data, textStatus, jqXHR) {
          _alert('주문이 취소 되었습니다');
          return location.href = '/';
        },
        error: function(jqXHR, textStatus, errorThrown) {
          return alert('error', jqXHR.responseJSON.error);
        },
        complete: function(jqXHR, textStatus) {
          return $this.removeClass('disabled');
        }
      });
    });
    clothes = [];
    $('.input-clothe').each(function(i, el) {
      return clothes.push($(el).data('clothe-no'));
    });
    $('#input-clothe-no').focus();
    $('#btn-clothe-no').click(function(e) {
      return $('#form-clothe-no').trigger('submit');
    });
    $('#form-clothe-no').submit(function(e) {
      var clothe_no, found;
      e.preventDefault();
      clothe_no = $('#input-clothe-no').val();
      $('#input-clothe-no').val('').focus();
      found = _.find(clothes, function(val) {
        return val === clothe_no;
      });
      if (!found) {
        return alert("Not found " + clothe_no);
      }
      return $(".input-clothe[data-clothe-no=" + found + "]").attr('checked', true);
    });
    return $('#form-return').submit(function(e) {
      var action, clothes_no;
      clothes_no = [];
      $('.input-clothe:not(:checked)').each(function(i, el) {
        return clothes_no.push($(el).data('clothe-no'));
      });
      console.log($.putUrlVars({
        clothes: clothes_no.join()
      }));
      if ($('.input-clothe').length !== $('.input-clothe:checked').length) {
        if (confirm('반납품목이 제대로 체크 되지 않았습니다. 계속하시겠습니까?')) {
          action = $('#form-return').attr('action');
          $('#form-return').attr('action', "" + action + "?" + ($.putUrlVars({
            missing_clothes: clothes_no.join()
          })));
          return true;
        } else {
          return false;
        }
      }
      return true;
    });
